import requests
from ai_coder.llm_client import call_llm

discord_webhook = "https://discord.com/api/webhooks/1234567890/ABCDEFGHIJKLMN"


def article_summary(url: str) -> str:
    response = requests.get(url)
    webpage_content = response.text
    summary = call_llm(
        webpage_content, sys_prompt="Summarize the webpage content", ai_input=""
    )
    return summary


def send_discord(message: str) -> None:
    payload = {"content": message}
    response = requests.post(discord_webhook, json=payload)
    if response.status_code == 204:
        print("Message sent successfully")
    else:
        print(f"Failed to send message: {response.status_code}")


def split_messages_to_send(message, delimiter="\n") -> None:
    messages = message.split(delimiter)
    chunk = ""
    for msg in messages:
        chunk += msg + delimiter
        if len(chunk) >= 2000:
            send_discord(chunk[: -len(msg) - len(delimiter)])
            chunk = msg + delimiter
    if chunk:
        send_discord(chunk)


